
public class lab2_a {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Using for loop: ");
		for(int i = 1; i < 11; i++) {
			System.out.println(i);
		}
		System.out.println("Using while loop: ");
		int n = 1;
		while(n <= 10) {
			System.out.println(n);
			n++;
		}
	}

}
